function opt = lbfgs_defaults(opt)

% fill in default options for lbfgs
if ~exist('opt','var') opt = struct(); end
opt=set_defaults(opt, 'm', 6,...
                      'ftol', 1e-5, ...
                      'maxiter', 0,...
                      'max_linesearch', 50,...
                      'display', 0,...
                      'epsg', 1e-5,...
                      'epsginfo', 1);
